<script setup>
import { onMounted, onUnmounted, ref } from 'vue'

const isMobile = ref(true)

const pageHeaderSection = ref(null)
const intro = ref(null)

const benefitsSection = ref(null)

const mapSection = ref(null)
const solarMap = ref(null)
const cursorX = ref(0)
const cursorY = ref(0)
const activeZoneNumber = ref(1)
const zoneTimer = ref(null)

onMounted(() => {
    isMobile.value = window.matchMedia('(max-width: 989px)').matches

    window.addEventListener('scroll', handleScroll)

    setTimeout(() => {
        if (pageHeaderSection.value) {
            pageHeaderSection.value.classList.add('is-loaded')
        }
    }, 100);

    setTimeout(() => {
        intro.value.classList.add('is-revealed')
    }, 400);

    if (isMobile.value) {
        autoSwitchZone()
    } else {
        window.addEventListener('mousemove', trackCursorPosition)

        setTimeout(() => {
            removeActivePanelAndZone()
        }, 100);
    }
})

onUnmounted(() => {
    if (isMobile.value) {
        clearInterval(zoneTimer.value)
    } else {
        window.removeEventListener('mousemove', trackCursorPosition)
    }

    window.removeEventListener('scroll', handleScroll)
})

function handleScroll() {
    if (window.innerHeight - benefitsSection.value.getBoundingClientRect().top >= benefitsSection.value.offsetHeight / 2) {
        benefitsSection.value.classList.add('is-revealed')
    }

    if (window.innerHeight - mapSection.value.getBoundingClientRect().top >= mapSection.value.offsetHeight / 2) {
        mapSection.value.classList.add('is-revealed')
    }
}

function autoSwitchZone() {
    zoneTimer.value = setInterval(() => {
        activeZoneNumber.value++

        if (activeZoneNumber.value > 3) {
            activeZoneNumber.value = 1
        }

        activatePanelAndZone()
    }, 3000);
}

function trackCursorPosition(e) {
    const solarMapPosition = solarMap.value.getBoundingClientRect()

    cursorX.value = e.clientX - solarMapPosition.left
    cursorY.value = e.clientY - solarMapPosition.top
}

function showPanelOnHover(e) {
    const targetId = e.target.id
    const activePanel = document.querySelector('.solar-map__panel.is-active')

    if (targetId) {
        const panelId = targetId.replace('zone', 'panel')

        if (activePanel) {
            activePanel.classList.remove('is-active')
        }

        document.getElementById(panelId).classList.add('is-active')
    }
}

function switchZoneOnClick(e) {
    activeZoneNumber.value = e.target.id.split('-')[1]

    clearInterval(zoneTimer.value)
    activatePanelAndZone()
}

function activatePanelAndZone() {
    document.querySelector('.solar-map__zone.is-active').classList.remove('is-active')
    document.getElementById('zone-' + activeZoneNumber.value).classList.add('is-active')

    document.querySelector('.solar-map__panel.is-active').classList.remove('is-active')
    document.getElementById('panel-' + activeZoneNumber.value).classList.add('is-active')
}

function removeActivePanelAndZone() {
    const activeZone = document.querySelector('.solar-map__zone.is-active')
    const activePanel = document.querySelector('.solar-map__panel.is-active')

    activeZone ? activeZone.classList.remove('is-active') : null
    activePanel ? activePanel.classList.remove('is-active') : null
}


</script>
